import React from 'react';
import { TouchableOpacity, StyleSheet, Text, View } from 'react-native';

class menuHeaderItem extends React.Component{
    constructor(props){
        super()
        this.props = props;
        this.state = {highlight:false}
    }

    render(){
        return (
            <View style={styles.c}>
                <TouchableOpacity onPress={this.toggleHighlight} style={styles.b}>
                    <Text>{this.props.text}</Text>
                </TouchableOpacity>
                {this.checkHighlight()}
            </View>
            
        );
    }

    toggleHighlight = () => {
        this.setState({highlight:!this.state.highlight});
    }

    checkHighlight(){
        if(this.state.highlight === false){
            return <View style={styles.a}></View>
        }else{
            return <View style={styles.d}></View>
        }
    }
}

const styles = StyleSheet.create({
    c:{
        width:100,
        height:100,
    },
    b:{
        borderWidth:2,
        borderColor:'red',
        width:100,
        height:100,
        justifyContent: 'center', //Centered vertically
        alignItems: 'center', // Centered horizontally
        flex:1
    },
    a:{
        backgroundColor:'green',
        padding:2.5,
        width:100
    },
    d:{
        backgroundColor:'blue',
        padding:2.5,
        width:100
    }
});

export default menuHeaderItem;
